import { CircularProgress, Slide, TextField } from "@mui/material";
import { useEffect, useState } from "react";
import "./App.css";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import React from 'react';

function App() {
  const [cityName, setCityName] = useState("Astana");
  const [inputText, setInputText] = useState("");
  const [data, setData] = useState({});
  const [error, setError] = useState(false);
  const [loading, setLoading] = useState(true);
  const [isDay, setIsDay] = useState(true);
  const [bgClass, setBgClass] = useState("day-bg");
  const [hourlyWeather, setHourlyWeather] = useState([]);

  const handleSearch = (e) => {
    if (e.key === "Enter") {
      setCityName(e.target.value);
      setInputText("");
    }
  };

  const fetchWeatherData = () => {
    fetch(
      `https://api.openweathermap.org/data/2.5/weather?q=${cityName}&appid=189271b827844bff7388350c44848615&units=metric`
    )
      .then((res) => {
        if (res.status === 200) {
          error && setError(false);
          return res.json();
        } else {
          throw new Error("Something went wrong");
        }
      })
      .then((weatherData) => {
        setData(weatherData);

        const currentTime = new Date().getTime() / 1000;
        const sunriseTime = weatherData.sys.sunrise;
        const sunsetTime = weatherData.sys.sunset;
        const isDay = currentTime > sunriseTime && currentTime < sunsetTime;

        setIsDay(isDay);

        if (isDay) {
          setBgClass("day-bg");
        } else {
          setBgClass("night-bg");
        }

        // Fetch forecast data after receiving weather data
        return fetch(
          `https://api.openweathermap.org/data/2.5/forecast?q=${cityName}&appid=189271b827844bff7388350c44848615&units=metric`
        );
      })
      .then((res) => {
        if (res.status === 200) {
          error && setError(false);
          return res.json();
        } else {
          throw new Error("Something went wrong");
        }
      })
      .then((forecastData) => {
        setHourlyWeather(forecastData.list);
        setLoading(false);
      })
      .catch(() => setError(true));
  };

  useEffect(() => {
    fetchWeatherData();
  }, [cityName, error]);


  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3, // Задайте количество отображаемых элементов на одном слайде
    slidesToScroll: 1,
  };

  return (
    <div className={`bg_img ${bgClass}`}>
      {!loading ? (
        <>
          <div className="search-container">
            <TextField
              variant="filled"
              label="Search"
              className="input"
              error={error}
              value={inputText}
              onChange={(e) => setInputText(e.target.value)}
              onKeyDown={handleSearch}
            />
          </div>

          <h1 className="city">{data.name}</h1>

          <h1 className="temp">{data.main.temp.toFixed()} °C</h1>

          <div className="group">
            <img
              src={`http://openweathermap.org/img/wn/${data.weather[0].icon}@2x.png`}
              alt="status icon"
            />
            <h1>{data.weather[0].main}</h1>
          </div>

          {/* Обновленный блок для погоды по времени с использованием слайдера */}
          <Slider className="hourly-container" {...settings}>
            {/* Маппинг по данным о прогнозе */}
            {hourlyWeather.map((hourlyData, index) => (
              <div key={index} className="hourly-item">
                {/* Маппинг по промежуткам времени внутри слайдов */}
                {hourlyData.map((hour, idx) => (
                  <div key={idx} className="time-slot">
                    <p className="time">
                      {new Date(hour.dt * 1000).toLocaleTimeString([], {
                        hour: '2-digit',
                        minute: '2-digit',
                      })}
                    </p>
                    <div className="weather-info">
                      <img
                        src={`http://openweathermap.org/img/wn/${hour.weather[0].icon}@2x.png`}
                        alt=""
                      />
                      <p>{hour.main.temp.toFixed()} °C</p>
                    </div>
                  </div>
                ))}
              </div>
            ))}
          </Slider>

          <Slide direction="up" timeout={800} in={!loading}>
            <div className="box_container">
              <div className="box">
                <p>Humidity</p>
                <h1>{data.main.humidity.toFixed()}%</h1>
              </div>

              <div className="box">
                <p>Wind</p>
                <h1>{data.wind.speed.toFixed()} km/h</h1>
              </div>

              <div className="box">
                <p>Feels Like</p>
                <h1>{data.main.feels_like.toFixed()} °C</h1>
              </div>
            </div>
          </Slide>
        </>
      ) : (
        <CircularProgress />
      )}
    </div>
  );
}

export default App;
